package coching;

public class Grades {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int marks=40;
		if(marks>=90)
		{
			System.out.print("youur grades is A+");
			
		}
		else if(marks>=80)
		{
			System.out.print("youur grades is A");
		}
		else if(marks>=70)
		{
			System.out.print("youur grades is B+");
		}
		else if(marks>=60)
		{
			System.out.print("youur grades is B");
		}
		else if(marks>=50)
		{
			System.out.print("youur grades is C+");
		}
		else if(marks>=40)
		{
			System.out.print("youur grades is C");
		}
	}

}
